import React from 'react'
import { BrowserRouter as Router, Route} from "react-router-dom";
import Signup from './Components/Main/Signup'
import { Nav } from './Components/Header/Nav'
import {Home} from './Components/Main/Home'
import {Redigera} from './Components/Main/Redigera'
import Login from './Components/Main/Login'
import { isLoggedIn, getToken } from './Components/AuthHelper';

class App extends React.Component {
    state = {
        categorySelected : 0,
        selectedBeerName : '',
        searchTerm: ''
    }

    componentDidMount() {
        if (!isLoggedIn()) {
            this.props.history.replace('/login')
        } else {
            axios({
                method: 'get',
                url: 'http://localhost:3010/api/books',
                headers: {
                    authorization: 'Bearer ' + getToken()
                }
            }).then((result) => {
                console.log("app protected resource")
                console.log(result)
            })
        }
    }
    
    onCategoryChange = selectedCat => {
        console.log("selected category: " + selectedCat)
        this.setState({
            categorySelected : selectedCat
        })
    }

    updateSearchTerm = queryString => {
        this.setState({searchTerm: queryString})
    }

    render() {
        return (
            <div>
                <Router>
                    <div>
                        <Nav selectedCat={this.state.categorySelected} onSelect={this.onCategoryChange}/>
                        <Route exact path="/" component={Home} />
                        <Route path="/Login" component={Login} />
                        <Route path="/signup" component={Signup}/>
                    </div>
                </Router>
            </div>
        );
    }
}



export default App;
